			}

			Object args = functionContext.getArguments();

			if (args == null)
				throw new FunctionException("arguments is required");

			TextPageCriteria criteria = null;

			if(args instanceof PdxInstance)
			{
				PdxInstance  pdxInstance = (PdxInstance)args;

				try
